






window.addEventListener("DOMContentLoaded", () => {
  document.body.addEventListener("click", (event) => {
    const listDialog = document.querySelectorAll("[data-fii-component='model']");
    listDialog.forEach(dialog => {
      const btn = event.target.closest("[data-fii-trigger='model']");
      if (btn && dialog.id === btn.dataset.fiiTarget && btn.dataset.fiiAction === "open") {
        dialog.showModal();
      } else if (btn && dialog.id === btn.dataset.fiiTarget && btn.dataset.fiiAction === "close") {
        dialog.close();
      }
    });
  });
});

