(function () {
    function init() {
        document.querySelectorAll("[data-nano-trigger=\"accordion\"]").forEach(function (btn) {
            if (btn._nanoAccordion) return;
            btn._nanoAccordion = true;
            btn.addEventListener("click", function () {
                var item = btn.closest(".nano-accordion__item");
                if (!item) return;
                var container = item.closest("[data-nano-accordion]");
                var isGroup = container && container.getAttribute("data-nano-accordion") === "group";
                if (isGroup) {
                    (container.querySelectorAll(".nano-accordion__item") || []).forEach(function (other) {
                        if (other !== item) other.classList.remove("nano-accordion__item--open");
                    });
                }
                item.classList.toggle("nano-accordion__item--open");
                btn.setAttribute("aria-expanded", item.classList.contains("nano-accordion__item--open"));
            });
        });
    }
    if (document.readyState === "loading") document.addEventListener("DOMContentLoaded", init);
    else init();
})();





(function () {
    function copyCode(btn) {
        var block = btn.closest(".nano-code-block");
        if (!block) return;
        var target = block.querySelector("pre.nano-pre, samp.nano-samp");
        if (!target) return;
        var text = target.textContent || "";
        var label = btn.getAttribute("aria-label") || btn.textContent;
        navigator.clipboard.writeText(text).then(function () {
            var orig = btn.textContent;
            btn.textContent = "Copied!";
            btn.setAttribute("aria-label", "Copied!");
            setTimeout(function () {
                btn.textContent = orig;
                btn.setAttribute("aria-label", label);
            }, 1500);
        });
    }
    function init() {
        document.querySelectorAll("[data-nano-trigger=\"copy-code\"]").forEach(function (btn) {
            if (btn._nanoCopyCode) return;
            btn._nanoCopyCode = true;
            btn.addEventListener("click", function () { copyCode(btn); });
        });
    }
    if (document.readyState === "loading") document.addEventListener("DOMContentLoaded", init);
    else init();
})();


window.addEventListener("DOMContentLoaded", () => {
  document.body.addEventListener("click", (event) => {
    const listDialog = document.querySelectorAll("[data-nano-component='model']");
    listDialog.forEach(dialog => {
      const btn = event.target.closest("[data-nano-trigger='model']");
      if (btn && dialog.id === btn.dataset.nanoTarget && btn.dataset.nanoAction === "open") {
        dialog.showModal();
      } else if (btn && dialog.id === btn.dataset.nanoTarget && btn.dataset.nanoAction === "close") {
        dialog.close();
      }
    });
  });
});
window.addEventListener("DOMContentLoaded", function () {
  document.body.addEventListener("click", function (event) {
    var toggler = event.target.closest("[data-nano-trigger=\"navbar\"]");
    if (toggler) {
      var nav = toggler.nextElementSibling;
      if (nav && nav.classList.contains("nano-navbar__nav")) {
        nav.classList.toggle("nano-navbar__nav--open");
        return;
      }
    }
    var link = event.target.closest(".nano-navbar__nav .nano-navbar__link");
    if (link) {
      var openNav = link.closest(".nano-navbar__nav.nano-navbar__nav--open");
      if (openNav) openNav.classList.remove("nano-navbar__nav--open");
    }
  });
});


